    return {
        maxDatasources: 1, // Maximum allowed datasources for this widget, -1 - unlimited
        maxDataKeys: 1 //Maximum allowed data keys for this widget, -1 - unlimited
    }
};*/

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "title": {
                "title": "Widget title",
                "type": "string",
                "default": ""
            },
            
            "retrieveMethod": {
                "title": "Retrieve string value using method",
                "type": "string",
                "default": "rpc"
            },
            "retrieveAttributeKey": {
                "title": "Retrieve Attribute/Timeseries value key (only when subscribe for attribute/timeseries method)",
                "type": "string",
                "default": ""
            },
            "retrieveRPCMethod": {
                "title": "Retrieve value using RPC method (only when call RPC get value method)",
                "type": "string",
                "default": ""
            },
            "parseValueFunction": {
                "title": "Parse value function, f(data), returns string",
                "type": "string",
                "default": "return data; /* console.log(data); */ "
            },
            
            "updateMethod": {
                "title": "Update string value using method",
                "type": "string",
                "default": "rpc"
            },
            "updateAttributeKey": {
                "title": "Update Attribute/Timeseries value key (only when update for attribute/timeseries method)",
                "type": "string",
                "default": ""
            },
            "updateRPCMethod": {
                "title": "Update value using RPC method (only when call RPC set value method)",
                "type": "string",
                "default": ""
            },
            "convertValueFunction": {
                "title": "Convert value function, f(value), returns payload used by RPC set value method",
                "type": "string",
                "default": "return value; /* console.log(value); */"
            },

            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 5000
            },

            "options":{
                "type": "array",
                "title": "Options",
                "minItems": 1,
                "maxItems": 30,
                "items": {
                    "type": "object",
                    "properties": {
                        "optionLabel": {
                            "title": "Option label",
                            "type": "string",
                            "default": "Option label"
                        },
                        "attributeValue": {
                            "title": "Attribute value",
                            "type": "string",
                            "description": "Attribute value."
                        }
                    }, 
                    "required": [
                        "optionLabel",
                        "attributeValue"
                    ]
                }
            },
            "optionStyle":{
                "type": "object",
                "title": "Option Style",
                "properties": {
                    "orientation": {
                        "type": "string",
                        "title": "Orientation",
                        "default": "column"
                    },
                    "isRaised": {
                        "type": "boolean",
                        "title": "Raised",
                        "default": true
                    },
                    "checkedOption": {
                        "type": "object",
                        "title": "Checked Option Style",
                        "properties": {
                            "isPrimary": {
                                "type": "boolean",
                                "title": "Primary color",
                                "default": true
                            },
                            "bgColor": {
                                "type": "string",
                                "title": "Checked option background color",
                                "default": null
                            },
                            "textColor": {
                                "type": "string",
                                "title": "Checked option text color",
                                "default": null
                            }
                        }
                    },
                    "uncheckedOption": {
                        "type": "object",
                        "title": "Unchecked Option Style",
                        "properties": {
                            "isPrimary": {
                                "type": "boolean",
                                "title": "Primary color",
                                "default": true
                            },
                            "bgColor": {
                                "type": "string",
                                "title": "Unchecked option background color",
                                "default": null
                            },
                            "textColor": {
                                "type": "string",
                                "title": "Unchecked option text color",
                                "default": null
                            }
                        }
                    }
                },
                "required": [
                    "orientation",
                    "isRaised",
                    "checkedOption",
                    "uncheckedOption"
                ]
            },
            
            "required": [
                "retrieveMethod",
                "updateMethod", 
                "options",
                "optionStyle"
            ]
        }
    },
    "form": [
        "title",

        {
            "key": "retrieveMethod",
            "type": "rc-select",
            "multiple": false,
            "items": [
                {
                    "value": "none",
                    "label": "Don't retrieve"
                },
                {
                    "value": "attribute",
                    "label": "Subscribe for attribute"
                },
                {
                    "value": "timeseries",
                    "label": "Subscribe for timeseries"
                },
                {
                    "value": "rpc",
                    "label": "Call RPC get value method"
                }
            ]
        },
        "retrieveAttributeKey",
        "retrieveRPCMethod",
        {
            "key": "parseValueFunction",
            "type": "javascript"
        },
        
        {
            "key": "updateMethod",
            "type": "rc-select",
            "multiple": false,
            "items": [
                {
                    "value": "SHARED_SCOPE",
                    "label": "Update shared attribute"
                },
                {
                    "value": "SERVER_SCOPE",
                    "label": "Update server attribute"
                },
                {
                    "value": "timeseries",
                    "label": "Update timeseries"
                },
                {
                    "value": "rpc",
                    "label": "Call RPC set value method"
                }
            ]
        },
        "updateAttributeKey",
        "updateRPCMethod",
        {
            "key": "convertValueFunction",
            "type": "javascript"
        },
        
        "requestTimeout",
        
        {
            "key": "options",
            "items": [
                "options[]"
            ]
        }, 
        {
            "key": "optionStyle",
            "items" :[
                {
                    "key": "optionStyle.orientation",
                    "type": "rc-select",
                    "multiple": false,
                    "items": [{
                        "value": "column",
                        "label": "Column"
                    }, {
                        "value": "row",
                        "label": "Row"
                    }]
                },
                "optionStyle.isRaised",
                {
                    "key": "optionStyle.checkedOption",
                    "items": [
                        "optionStyle.checkedOption.isPrimary",
                        {
                            "key": "optionStyle.checkedOption.bgColor",
                            "type": "color"
                        },
                        {
                            "key": "optionStyle.checkedOption.textColor",
                            "type": "color"
                        }
                    ]
                }, {
                    "key": "optionStyle.uncheckedOption",
                    "items": [
                        "optionStyle.uncheckedOption.isPrimary",
                        {
                            "key": "optionStyle.uncheckedOption.bgColor",
                            "type": "color"
                        },
                        {
                            "key": "optionStyle.uncheckedOption.textColor",
                            "type": "color"
                        }
                    ]
                }
            ]
        }
    ]
}

