.card {
    width: 100%;
    height: 100%;
    border: 0px solid #E0E0E0;
    box-sizing: border-box;
}

.card .content {
    padding: 22px 10px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 100%;
    width: 100%;
    box-sizing: border-box;
}

.card .value {
    font-size: 1em;
    font-weight: bold;
    line-height: 1.5;
    text-align: center;
    animation: rainbow 5s infinite;
    /* Apply the rainbow animation */
}

.card .description {
    font-size: 0.8em;
    font-weight: normal;
    line-height: 1.5;
    color: #333333;
    text-align: left;
    opacity: 0.5;
}

@keyframes rainbow {
    0% {
        color: red;
    }

    20% {
        color: yellow;
    }

    40% {
        color: lime;
    }

    60% {
        color: aqua;
    }

    80% {
        color: blue;
    }

    100% {
        color: red;
    }
}

