function openDeleteEntityDialog() {
    let title = 'Delete claiming data for ' + entityName;
    let content = 'Are you sure you want to delete claiming data for ' + entityName + '?';
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function(result) {
            if (result) {
                deleteSecretKey();
            }
        }
    );
}

function deleteSecretKey() {
    const attributesArray = [{key: 'claimingData', value: null}];
    return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray).subscribe(
        function success() {
            widgetContext.updateAliases();
        },
        function fail() {
            showErrorDialog();
        }
    );
}

function showErrorDialog() {
    let title = 'Error';
    let content = 'An error occurred while deleting the claiming data. Please try again.';
    dialogs.alert(title, content, 'CLOSE').subscribe(
        function(result) {}
    );
}

