let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let utils = $injector.get(widgetContext.servicesMap.get('utils'));

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;

    vm.editSecretKeyFormGroup = vm.fb.group({
        secretKey: ['']
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        saveAttributes(entityId).subscribe(
            function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    vm.generateKey = function() {
        const secret = generateSecret(16);
        vm.editSecretKeyFormGroup.patchValue({
            secretKey: secret
        });
        vm.editSecretKeyFormGroup.markAsDirty();
    }

    function generateSecret(length) {
      if (!length || length < 0) {
        length = 1;
      }
      const l = length > 10 ? 10 : length;
      const str = Math.random().toString(36).substr(2, l);
      if (str.length >= length) {
        return str;
      }
      return str.concat(generateSecret(length - str.length));
    }

    function getEntityInfo() {
        attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['claimingData']).subscribe(
            function (data) {
                if (data.length) {
                    const secretKey = JSON.parse(data[0].value).secretKey;
                    vm.editSecretKeyFormGroup.patchValue({
                        secretKey: secretKey
                    }, {emitEvent: false});    
                }
            }
        );
    }

    function saveAttributes(entityId) {
        const secretKey = vm.editSecretKeyFormGroup.get('secretKey').value;
        const attributesArray = [{
            key: 'claimingData',
            value: JSON.stringify({
                secretKey: secretKey,
                expirationTime: 1893448800000
            }) 
        }];
        return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
    }
}

