function append_primary_value_with_unit(label, value, decimals, unit)
{
    if (isPrimaryValueDisplayed===true && label) {
        return '<div class="primary-label">' + label + '</div>\n' + 
               '<p><span class="primary-value">' + convert_value_to_string(value,decimals) + '</span>' +
