               '<span class="secondary-unit">' + (unit?unit:'°C') + '</span>' +'</p>\n';
    } else {
        return '';
    }
}

function convert_value_to_string(value, decimals)
{
    if (!value) {
        return '';
    }
    if (value==='') {
        return '';
    }
    let my_decimals = decimals ? decimals : 1;
    return Number.parseFloat(value).toFixed(decimals);
}

