/* ui-ngx/src/app/shared/components/dashboard-select.components.ts/DashboardSelectComponent/getDashboards() */
function getDashboardsObservable() /*: Observable<PageData<DashboardInfo>>*/
{
    let dashboardService = widgetContext.$scope.$injector.get(widgetContext.servicesMap.get('dashboardService'));

    let pageLink = {}; /*: PageLink;*/
    pageLink.textSearch = null; //: string;
    pageLink.pageSize = 1000; //: number;
    pageLink.page = 0; //: number;
    pageLink.sortOrder = null; //: SortOrder;
    pageLink.toQuery = function() {
        let query = `?pageSize=${this.pageSize}&page=${this.page}`;
        if (this.textSearch && this.textSearch.length) {
          const textSearch = encodeURIComponent(this.textSearch);
          query += `&textSearch=${textSearch}`;
        }
        if (this.sortOrder) {
          query += `&sortProperty=${this.sortOrder.property}&sortOrder=${this.sortOrder.direction}`;
        }
        return query;
    }

    let dashboardsObservable; /*: Observable<PageData<DashboardInfo>>;*/
    const authUser = widgetContext.currentUser;

    if (authUser.authority === 'CUSTOMER_USER'/*Authority.CUSTOMER_USER*/) {
        if (authUser.customerId /*&& authUser.customerId !== NULL_UUID*/) {
            dashboardsObservable = dashboardService.getCustomerDashboards(authUser.customerId, pageLink, {ignoreLoading: true});
        } else {
            dashboardsObservable = null; //of(emptyPageData());
        }
    } else if (authUser.authority === 'SYS_ADMIN' /*Authority.SYS_ADMIN*/) {
        if (authUser.tenantId) {
            dashboardsObservable = dashboardService.getTenantDashboardsByTenantId(authUser.tenantId, pageLink, {ignoreLoading: true});
        } else {
          dashboardsObservable = null; //of(emptyPageData());
        }
    } else /* 'TENANT_ADMIN' */ {
        dashboardsObservable = dashboardService.getTenantDashboards(pageLink,
          {ignoreLoading: true});
    }

    //console.log("dashboardsObservable", dashboardsObservable);
    return dashboardsObservable;
}

/* ui-ngx/src/app/modules/home/components/widget/widget.compnent.ts/WidgetComponet/handleWidgetAction()/case WidgetActionType.openDashboard: */
function my_handleWidgetAction(targetDashboardId, targetDashboardStateId,
                       $event/*: Event*/, /*descriptor: WidgetActionDescriptor,*/
                       entityId/*?: EntityId*/, entityName/*?: string*/,
                       additionalParams/*?: any*/, entityLabel/*?: string*/)
{
    let descriptor = {}; /* : WidgetActionDescriptor */
    descriptor.type = 'openDashboard';
    descriptor.stateEntityParamName = null; /* special entity param name */
    descriptor.setEntityId = true;  /* set entityId for new dashboard */
    descriptor.targetDashboardId = targetDashboardId;
    descriptor.targetDashboardStateId = targetDashboardStateId;
    descriptor.openNewBrowserTab = false;

    // console.log("my_entityId:", entityId);
    // console.log("my_entityName:", entityName);
    // console.log("my_entityLabel:", entityLabel);
    // console.log("descriptor:", descriptor);

    widgetContext.actionsApi.handleWidgetAction($event, descriptor,
                       entityId, entityName, additionalParams, entityLabel) 
}
