settings.markerImageFunction:
var res = {
    url: images[0],
    size: 34
};
var dailyConsumptionThresholdExceeded = (dsData[dsIndex]['dailyConsumptionThresholdExceeded'] && dsData[dsIndex]['dailyConsumptionThresholdExceeded'] === 'true');
var weeklyConsumptionThresholdExceeded = (dsData[dsIndex]['weeklyConsumptionThresholdExceeded'] && dsData[dsIndex]['weeklyConsumptionThresholdExceeded'] === 'true');
var inactive = (dsData[dsIndex]['deviceInactive'] && dsData[dsIndex]['deviceInactive'] === 'true');
var lowBattery = (dsData[dsIndex]['lowBattery'] && dsData[dsIndex]['lowBattery'] === 'true');
var lowTemperature = (dsData[dsIndex]['temperatureUnderThreshold'] && dsData[dsIndex]['temperatureUnderThreshold'] === 'true');

if (inactive) {
    res.url = images[2];
} else if (dailyConsumptionThresholdExceeded || weeklyConsumptionThresholdExceeded || 
            lowBattery || lowTemperature) {
    res.url = images[1];
}
return res;

settings.tooltipFunction:
var dailyConsumptionThresholdExceeded = (dsData[dsIndex]['dailyConsumptionThresholdExceeded'] && dsData[dsIndex]['dailyConsumptionThresholdExceeded'] === 'true');
var weeklyConsumptionThresholdExceeded = (dsData[dsIndex]['weeklyConsumptionThresholdExceeded'] && dsData[dsIndex]['weeklyConsumptionThresholdExceeded'] === 'true');
var inactive = (dsData[dsIndex]['deviceInactive'] && dsData[dsIndex]['deviceInactive'] === 'true');
var lowBattery = (dsData[dsIndex]['lowBattery'] && dsData[dsIndex]['lowBattery'] === 'true');
var lowTemperature = (dsData[dsIndex]['temperatureUnderThreshold'] && dsData[dsIndex]['temperatureUnderThreshold'] === 'true');

var generalInfo = '<div><b style="line-height: 2; font-size: 14px;">${entityName}</b><br/><span style="line-height: 2;">Address: ${address}</span></div>';
var telemetryInfo = '<div><span>Consumed per week: ${weeklyConsumption:2} m<sup style="font-size: 9px;">3</sup></span><br/><span>Consumed per day: ${dailyConsumption:2} m<sup style="font-size: 9px;">3</sup></span><br/><span>Temperature: ${temperature:1}</span></div>';

var alarmsInfo = '';
if (inactive) {
    alarmsInfo += '<br/><span>Device inactive.</span>';
} else {
    if (dailyConsumptionThresholdExceeded) {
        alarmsInfo += '<br/><span>Daily consumption threshold exceeded.</span>';
    } 
    if (weeklyConsumptionThresholdExceeded) {
        alarmsInfo += '<br/><span>Weekly consumption threshold exceeded.</span>';
    } 
    if (lowBattery) {
        alarmsInfo += '<br/><span>Low battery.</span>';
    } 
    if (lowTemperature) {
        alarmsInfo += '<br/><span>Low temperature.</span>';
    }
}

var label = generalInfo + telemetryInfo;

if (alarmsInfo) {
    label += '<div style="margin-top: 8px;"><span>Alarms:</span>' + alarmsInfo + '</div>'
}

return label;

