headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog')),
    deviceService = $injector.get(widgetContext.servicesMap.get('deviceService')),
    attributeService = $injector.get(widgetContext.servicesMap.get('attributeService')),
    entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService')),
    entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
    entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
    // userPermissionsService = $injector.get(widgetContext.servicesMap.get('userPermissionsService'));

openAddDeviceDialog();

function openAddDeviceDialog() {
    customDialog.customDialog(htmlTemplate, AddDeviceDialogController).subscribe();
}

function AddDeviceDialogController(instance) {
    let vm = instance;

    vm.deviceType = 'Water Meter';

    // vm.ownerId = userPermissionsService.getUserOwnerId();
    // vm.excludeOwnerIds = [vm.ownerId.id];

    vm.addDeviceFormGroup = vm.fb.group({
        deviceName: ['', [vm.validators.required]],
        deviceLabel: [null],
        attributes: vm.fb.group({
            address: [null]
        }),
        targetOwner: [null, [vm.validators.required]]
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = () => {
        vm.loading = true;
        vm.addDeviceFormGroup.markAsPristine();
        const formValues = vm.addDeviceFormGroup.value;
        let device = {
            name: formValues.deviceName,
            type: vm.deviceType,
            label: formValues.deviceLabel
        };
        let targetOwner = formValues.targetOwner;
        if (targetOwner.id.entityType === 'CUSTOMER') {
            device.customerId = targetOwner.id;
        }
        deviceService.saveDevice(device).subscribe(
            (device) => {
                widgetContext.rxjs.forkJoin([
                    saveAttributes(device.id),
                    addRelation(device.id, targetOwner)
                ]).subscribe(
                    () => {
                        vm.loading = false;
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    },
                    () => {
                        vm.loading = false;
                    }
                );
            },
            () => {
                vm.loading = false;
            }
        );
    };

    init();

    function init() {
        vm.availableOwners = [
            {
                name: 'Tenant',
                id: {id: widgetContext.currentUser.tenantId, entityType: 'TENANT' }
            }
        ];
        getEntityGroupByName('Water Metering', 'CUSTOMER').subscribe((waterMeteringGroup) => {
          if (waterMeteringGroup) {
              var query = {
                  entityFilter: {
                      type: 'entityGroup',
                      groupType: 'CUSTOMER',
                      entityGroup: waterMeteringGroup.id.id
                  },
                  pageLink: {
                      pageSize: 100,
                      page: 0
                  },
                  entityFields: [
                      {
                          key: 'name',
                          type: 'ENTITY_FIELD'
                      }
                  ]
              };
              entityService.findEntityDataByQuery(query).subscribe(
                  (data) => {
                      const availableCustomers = data.data.map((entityData) => {
                          return {
                              id: entityData.entityId,
                              name: entityData.latest["ENTITY_FIELD"].name.value
                          };
                      });
                      vm.availableOwners = vm.availableOwners.concat(availableCustomers);
                  }
              );
          }
      });
    }

    function saveAttributes(entityId) {
        let attributes = vm.addDeviceFormGroup.get('attributes').value;
        attributes.inactivityTimeout = 7200000;
        let attributesArray = [];
        for (let key in attributes) {
            if (attributes[key] !== null) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function addRelation(deviceId, targetOwner) {
        if (targetOwner.id.entityType === 'TENANT') {
            return widgetContext.rxjs.of(null);
        } else {
            let relation = {
                type: 'Owns',
                typeGroup: 'COMMON',
                from: targetOwner.id,
                to: deviceId
            };

            return entityRelationService.saveRelation(relation);
        }
    }

    function getEntityGroupByName(groupName, groupType) {
      var entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
      return entityGroupService.getEntityGroupsByPageLink(entityGroupsPageLink, groupType, {ignoreLoading: true}).pipe(
          widgetContext.rxjs.map((data) => {
            if (data.data.length) {
                return data.data.find((group) => group.name === groupName);
            } else {
                return null;
            }
          })
      );
  }

}

