let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog')),
    customerService = $injector.get(widgetContext.servicesMap.get('customerService')),
    userService = $injector.get(widgetContext.servicesMap.get('userService')),
    attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditCustomerDialog();

function openEditCustomerDialog() {
    customDialog.customDialog(htmlTemplate, EditCustomerDialogController).subscribe();
}

function EditCustomerDialogController(instance) {
    let vm = instance;

    vm.attributes = {};

    vm.editCustomerFormFormGroup = vm.fb.group({
        title: ['', [vm.validators.required]],
        email: ['', [vm.validators.required, vm.validators.pattern(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\_\-0-9]+\.)+[a-zA-Z]{2,}))$/)]],
        firstName: [''],
        lastName: [''],
        phone: ['', [vm.validators.pattern(/^[0-9-+\s()]*$/)]],
        address: ['']
    });

    getCustomerInfo();

    vm.cancel = () => {
        vm.dialogRef.close(null);
    };

    vm.save = () => {
        vm.loading = true;
        vm.editCustomerFormFormGroup.markAsPristine();
        const formValues = vm.editCustomerFormFormGroup.value;
        vm.customer.title = formValues.title;
        vm.customer.email = formValues.email;
        vm.customer.phone = formValues.phone;
        vm.customer.address = formValues.address;
        if (vm.user) {
            vm.user.email = formValues.email;
            vm.user.firstName = formValues.firstName;
            vm.user.lastName = formValues.lastName;
        }
        vm.attributes.firstName = formValues.firstName;
        vm.attributes.lastName = formValues.lastName;

        customerService.saveCustomer(vm.customer).subscribe(
            (customer) => {
                if (vm.user) {
                    userService.saveUser(vm.user).subscribe(
                        () => {
                            saveAttributes(vm.customer.id).subscribe(
                                () => {
                                    widgetContext.updateAliases();
                                    vm.loading = false;
                                    vm.dialogRef.close(null);
                                }
                            );
                        },
                        () => {
                            customerService.saveCustomer(vm.oldCustomerInfo).subscribe(
                                () => {
                                    vm.loading = false;
                                });
                        });
                } else {
                    saveAttributes(vm.customer.id).subscribe(
                        () => {
                            widgetContext.updateAliases();
                            vm.loading = false;
                            vm.dialogRef.close(null);
                        }
                    );
                }
            },
            () => {
                vm.loading = false;
            }
        );
    };

    function getCustomerInfo() {
        vm.loading = true;
        vm.attributes = {};
        vm.customer = null;
        vm.user = null;
        customerService.getCustomer(entityId.id).subscribe(
            (customer) => {
                vm.customer = customer;
                vm.oldCustomerInfo = Object.assign({}, customer);

                const customerUserPageLink = {
                    toQuery: function() {
                        return '?pageSize=1&page=0&textSearch='+encodeURIComponent(customer.title);
                    }
                }; 


                widgetContext.rxjs.forkJoin([
                    attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['firstName', 'lastName']),
                    userService.getCustomerUsers(customer.id.id, customerUserPageLink)]).subscribe(
                    (response) => {
                        let attributes = response[0];
                        if (attributes.length) {
                            getEntityAttributes(attributes);
                        }
                        if (response[1].data.length) {
                            vm.user = response[1].data[0];
                            vm.attributes.firstName = vm.user.firstName;
                            vm.attributes.lastName = vm.user.lastName;
                        }
                        vm.editCustomerFormFormGroup.patchValue({
                            title: vm.customer.title,
                            email: vm.customer.email,
                            firstName: vm.attributes.firstName,
                            lastName: vm.attributes.lastName,
                            phone: vm.customer.phone,
                            address: vm.customer.address
                        }, {emitEvent: false});
                        vm.loading = false;
                   } 
                );
            }
        );
    }

    function getEntityAttributes(attributes) {
        for (let i = 0; i < attributes.length; i++) {
            vm.attributes[attributes[i].key] = attributes[i].value; 
        }
    }

    function saveAttributes(entityId) {
        let attributesArray = [];
        for (let key in vm.attributes) {
            if (vm.attributes[key] !== null) {
                attributesArray.push({key: key, value: vm.attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

}

