var weeklyConsumptionThresholdExceeded = (dsData[dsIndex]['weeklyConsumptionThresholdExceeded'] && dsData[dsIndex]['weeklyConsumptionThresholdExceeded'] === 'true');
var inactive = (dsData[dsIndex]['deviceInactive'] && dsData[dsIndex]['deviceInactive'] === 'true');
var lowBattery = (dsData[dsIndex]['lowBattery'] && dsData[dsIndex]['lowBattery'] === 'true');
var lowTemperature = (dsData[dsIndex]['temperatureUnderThreshold'] && dsData[dsIndex]['temperatureUnderThreshold'] === 'true');

if (inactive) {
    res.url = images[2];
} else if (dailyConsumptionThresholdExceeded || weeklyConsumptionThresholdExceeded || 
            lowBattery || lowTemperature) {
    res.url = images[1];
}
return res;

