        getEntityInfo();
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = () => {
        vm.loading = true;
        vm.editDeviceFormGroup.markAsPristine();
        saveDevice().subscribe(
            (/*device*/) => {
                changeDeviceOwner().subscribe(
                    () => {
                        vm.loading = false;
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    },
                    () => {
                        vm.loading = false;
                    }
                );
            },
            () => {
                vm.loading = false;
            }
        );
    };

    function init() {
        vm.availableOwners = [
            {
                name: 'Tenant',
                id: {id: widgetContext.currentUser.tenantId, entityType: 'TENANT' }
            }
        ];
        return getEntityGroupByName('Water Metering', 'CUSTOMER').pipe(
            widgetContext.rxjs.switchMap((waterMeteringGroup) => {
               if (waterMeteringGroup) {
                    var query = {
                      entityFilter: {
                          type: 'entityGroup',
                          groupType: 'CUSTOMER',
                          entityGroup: waterMeteringGroup.id.id
                      },
                      pageLink: {
                          pageSize: 100,
                          page: 0
                      },
                      entityFields: [
                          {
                              key: 'name',
                              type: 'ENTITY_FIELD'
                          }
                      ]
                  };
                  return entityService.findEntityDataByQuery(query).pipe(
                      widgetContext.rxjs.switchMap((data) => {
                          const availableCustomers = data.data.map((entityData) => {
                              return {
                                  id: entityData.entityId,
                                  name: entityData.latest["ENTITY_FIELD"].name.value
                              };
                          });
                          vm.availableOwners = vm.availableOwners.concat(availableCustomers);
                          return widgetContext.rxjs.of(null);
                      })
                  );
            } else {
                return widgetContext.rxjs.of(null);
            }
        }
      ));
    }

    function getEntityInfo() {
        vm.loading = true;
        deviceService.getDevice(entityId.id).subscribe(
            (device) => {
                vm.device = device;
                let ownerId = vm.device.ownerId;
                let targetOwner = vm.availableOwners.find((owner) => owner.id.id === ownerId.id);
                vm.editDeviceFormGroup.patchValue({
                    deviceName: vm.device.name,
                    deviceLabel: vm.device.label,
                    targetOwner: targetOwner
                }, {emitEvent: false});
                vm.loading = false;
            }
        );
    }

    function saveDevice() {
        const formValues = vm.editDeviceFormGroup.value;
        if (vm.device.name !== formValues.deviceName || (vm.device.label !== formValues.deviceLabel)) {
            vm.device.name = formValues.deviceName;
            vm.device.label = formValues.deviceLabel;
            return deviceService.saveDevice(vm.device);
        }
        return widgetContext.rxjs.of([]);
    }

    function changeDeviceOwner() {
        let newOwner = vm.editDeviceFormGroup.get('targetOwner').value;
        if (newOwner.id !== vm.device.ownerId.id) {
            return widgetContext.rxjs.forkJoin([
                entityGroupService.changeEntityOwner(newOwner.id, vm.device.id),
                saveRelations(vm.device.id, vm.device.ownerId, newOwner.id)
            ]);
        }
        return widgetContext.rxjs.of([]);
    }

    function saveRelations(deviceId, currentOwnerId, newOwnerId) {
        let tasks = [
            deleteRelation(deviceId, currentOwnerId),
            addRelation(deviceId, newOwnerId)
        ];
        return widgetContext.rxjs.forkJoin(tasks);
    }

    function addRelation(deviceId, targetOwnerId) {
        if (targetOwnerId.entityType === 'TENANT') {
            return widgetContext.rxjs.of(null);
        } else {
            let relation = {
                type: 'Owns',
                typeGroup: 'COMMON',
                from: targetOwnerId,
                to: deviceId
            };

            return entityRelationService.saveRelation(relation);
        }
    }

    function deleteRelation(deviceId, currentOwnerId) {
        if (currentOwnerId.entityType === 'TENANT') {
            return widgetContext.rxjs.of(null);
        } else {
            let relation = {
                type: 'Owns',
                from: currentOwnerId,
                to: deviceId
            };

            return entityRelationService.deleteRelation(relation.from, relation.type, relation.to);
        }
    }

    function getEntityGroupByName(groupName, groupType) {
      var entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
      return entityGroupService.getEntityGroupsByPageLink(entityGroupsPageLink, groupType, {ignoreLoading: true}).pipe(
          widgetContext.rxjs.map((data) => {
            if (data.data.length) {
                return data.data.find((group) => group.name === groupName);
            } else {
                return null;
            }
          })
      );
   }
}

