        if (updateValue) {
            if (typeof updateValue === "string") {
                if (updateValue==="true" || updateValue==="True" || updateValue==="TRUE") {
                    return true;
                } else if (updateValue==="false" || updateValue==="False" || updateValue==="FALSE") {
