descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "title": {
                "title": "Widget title",
                "type": "string",
                "default": ""
            },

            "retrieveAttributeKey": {
                "title": "Device attribute key",
                "type": "string",
                "default": ""
            },
            "retrieveAttributeKeyOfMinValue": {
                "title": "Device attribute key of Min value",
                "type": "string",
                "default": ""
            },
            "retrieveAttributeKeyOfMaxValue": {
                "title": "Device attribute key of Max value",
                "type": "string",
                "default": ""
            },
            "retrieveAttributeKeyOfStepValue": {
                "title": "Device attribute key of Step value",
                "type": "string",
                "default": ""
            },
            "retrieveAttributeKeyOfUnit": {
                "title": "Device attribute key of Unit",
                "type": "string",
                "default": ""
            },

            "updateMethod": {
                "title": "Update double value using method",
                "type": "string",
                "default": "rpc"
            },
            "updateAttributeKey": {
                "title": "Update Attribute/Timeseries value key (only when update for attribute/timeseries method)",
                "type": "string",
                "default": ""
            },
            "updateRPCMethod": {
                "title": "Update value using RPC method (only when call RPC set value method)",
                "type": "string",
                "default": ""
            },
            "convertValueFunction": {
                "title": "Convert value function, f(value), returns payload used by RPC set value method",
                "type": "string",
                "default": "return value; /* console.log(value); */"
            },

            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 5000
            },

            "required": [
                "retrieveAttributeKey",
                "retrieveAttributeKeyOfMinValue",
                "retrieveAttributeKeyOfMaxValue",
                "retrieveAttributeKeyOfStepValue",
                "updateMethod"
            ]
        }
    },
    "form": [
        "title",

        "retrieveAttributeKey",
        "retrieveAttributeKeyOfMinValue",
        "retrieveAttributeKeyOfMaxValue",
        "retrieveAttributeKeyOfStepValue",
        "retrieveAttributeKeyOfUnit",

        {
            "key": "updateMethod",
            "type": "rc-select",
            "multiple": false,
            "items": [
                {
                    "value": "SHARED_SCOPE",
                    "label": "Update shared attribute"
                },
                {
                    "value": "SERVER_SCOPE",
                    "label": "Update server attribute"
                },
                {
                    "value": "timeseries",
                    "label": "Update timeseries"
                },
                {
                    "value": "rpc",
                    "label": "Call RPC set value method"
                }
            ]
        },
        "updateAttributeKey",
        "updateRPCMethod",
        {
            "key": "convertValueFunction",
            "type": "javascript"
        },

        "requestTimeout"
    ]
}

