descriptor.controllerScript:
// v.1.0.0, 2023-05-25 17:24

// Based on Segment switch of string value

let valueSubscription;

self.onInit = function() {
    self.ctx.ngZone.run(function() {
        init();
        self.ctx.detectChanges(true);
    });
};

function init() {
    let utils = self.ctx.$scope.$injector.get(self.ctx
        .servicesMap.get('utils'));
    //let types       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('types'));
    //let toast       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('toast'));
    let translate = self.ctx.$scope.$injector.get(self.ctx
        .servicesMap.get('translate'));
    //let $q          = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('$q'));
    let http = self.ctx.$scope.$injector.get(self.ctx
        .servicesMap.get('http'));
    let attributeService = self.ctx.$scope.$injector.get(
        self.ctx.servicesMap.get('attributeService'));
    let rpcUpdateStatus = {};
    let settings = utils.deepClone(self.ctx.settings) || {};

    self.ctx.$scope.toastTargetId = 'input-widget' + utils
        .guid();
    self.ctx.$scope.showTitle = settings.title && settings
        .title.length ? true :
        false; ////utils.defaultValue(settings.title, true);
    self.ctx.$scope.title = settings.title;
    self.ctx.$scope.convertedValue = 0;

    // let stateParams = {};
    // if (!self.ctx.isEdit && self.ctx.stateController) {
    //     stateParams = self.ctx.stateController
    //         .getStateParams();
    // }
    let widgetContext = self.ctx;

    let convertRetrievedValueFunction = (retrievedValue) =>
        retrievedValue; // Retrieved data
    if (settings.convertRetrievedValueFunction && settings
        .convertRetrievedValueFunction.length) {
        try {
            convertRetrievedValueFunction = new Function(
                'retrievedValue', settings
                .convertRetrievedValueFunction);
        } catch (e) {
            convertRetrievedValueFunction = (
                retrievedValue) => retrievedValue;
        }
    }

    //self.ctx.$scope.settingList = settings.settingList;
    self.ctx.$scope.settingList = [];
    for (const item of settings.settingList) {
        self.ctx.$scope.settingList.push({
            "icon": item.icon,
            "text": item.text,
            "isDisabledFunc": new Function(
                'convertedValue', item
                .isDisabledFunc),
            "customActionFunc": new Function(
                'convertedValue', 'widgetContext',
                'openRightLayout', item
                .customActionFunc)
        });
    }

    let lastAttrValue = {}; //let lastAttrValue;
    function onDataUpdatedForSubscribe(subscription,
        apply) {
        let convertedValue;

        for (let i = 0; subscription.data && i <
            subscription.data.length; i++) {
            let keyName = subscription.data[i].dataKey.name;
            if (subscription.data[i].data && subscription
                .data[i].data.length > 0) {
                let KeyValue = subscription.data[i].data[0][1];
                //if (settings.retrievedAttributeKey && keyName === settings.retrievedAttributeKey) {
                    try {
                        convertedValue = convertRetrievedValueFunction(KeyValue); ////angular.fromJson(newValue)
                    } catch (e) {
                        self.ctx.$scope.error =
                            'convertRetrievedValueFunction(value) error!'
                    }
                    //if (typeof KeyValue === 'number' && isFinite(KeyValue)) {
                    //    convertedValue = KeyValue;
                    //}
                //} else {
                //    console.log("unkown data:subscription.data[%d]", i);
                //    console.log(subscription.data[i]);
                //}
            }
        }

        if (convertedValue) {
            self.ctx.$scope.convertedValue =
                convertedValue; //String(convertedValue);
            //refreshTextColor();
        }

        if (apply) {
            self.ctx
                .detectChanges(); //self.ctx.$scope.$digest();
        }
    }

    function onDataUpdateErrorForSubscribe(subscription,
        e) {
        var exceptionData = utils.parseException(e);
        var errorText = exceptionData.name;
        if (exceptionData.message) {
            errorText += ': ' + exceptionData.message;
        }
        self.ctx.$scope.error = errorText;
        console.log(
            "onDataUpdateErrorForSubscribe(): errorText=",
            errorText);
    }

    function subscribeAttributes(type, entityType, entityId,
        key) { //retrievedMethod,
        var valueSubscriptionInfo = [{
            type: type,
            entityType: entityType,
            entityId: entityId
        }];

        //if (retrievedMethod == 'attribute') {
        valueSubscriptionInfo[0].attributes = [{
            name: key
        }];
        //} else {
        //    valueSubscriptionInfo[0].timeseries = [{name: key}];
        //}

        var subscriptionOptions = {
            callbacks: {
                onDataUpdated: onDataUpdatedForSubscribe,
                onDataUpdateError: onDataUpdateErrorForSubscribe
            }
        };
        self.ctx.subscriptionApi.createSubscriptionFromInfo(
                'latest', valueSubscriptionInfo,
                subscriptionOptions, false, true)
            .subscribe( //types.widgetType.latest.value
                (subscription) => {
                    valueSubscription = subscription;
                }
            );
    }

    function subscribeTimeseries(type, entityType, entityId,
        key) { //retrievedMethod,
        var valueSubscriptionInfo = [{
            type: type,
            entityType: entityType,
            entityId: entityId
        }];

        //if (retrievedMethod == 'attribute') {
        //    valueSubscriptionInfo[0].attributes = [{name: key}];
        //} else {
        valueSubscriptionInfo[0].timeseries = [{
            name: key
        }];
        //}

        var subscriptionOptions = {
            callbacks: {
                onDataUpdated: onDataUpdatedForSubscribe,
                onDataUpdateError: onDataUpdateErrorForSubscribe
            }
        };
        self.ctx.subscriptionApi.createSubscriptionFromInfo(
                'latest', valueSubscriptionInfo,
                subscriptionOptions, false, true)
            .subscribe( //types.widgetType.latest.value
                (subscription) => {
                    valueSubscription = subscription;
                }
            );
    }


    // RPC retrieved value
    function rpcRetrievedValue(method, timeout) {
        if (method && method.length) {
            self.ctx.$scope.error = '';
            self.ctx.controlApi.sendTwoWayCommand(method,
                null, timeout).subscribe(
                function success(responseBody) {
                    ////self.ctx.$scope.convertedValue = responseBody;
                    self.ctx.$scope.convertedValue =
                        convertRetrievedValueFunction(
                            responseBody);

                    ////self.ctx.$scope.$digest(); // Error: $digest already in progress
                },
                function fail() {
                    self.ctx.$scope.error = self.ctx
                        .defaultSubscription
                        .rpcErrorText;
                }
            );
        } else {
            self.ctx.$scope.error =
                "retrievedRPCMethodName is null.";
        }
    }

    self.ctx.$scope.isDsiabledListOption = function(index) {
        //console.log("isDsiabledListOption()", index);
        //if (self.ctx.$scope.settingList[index].isDisabledFunc) {
        return self.ctx.$scope.settingList[index]
            .isDisabledFunc(self.ctx.$scope
                .convertedValue);
        //}
    }
    self.ctx.$scope.listSelectionChange = function(index) {
        //console.log("listSelectionChange()", index);
        self.ctx.$scope.settingList[index]
            .customActionFunc(self.ctx.$scope
                .convertedValue, widgetContext, false);
    }

    // retrieved value
    var firstRetrieveValue = true;
    function retrieveValue() {
        let rpcEnabled = self.ctx.defaultSubscription
            .rpcEnabled;
        let isSimulated = self.ctx.$scope.widgetEditMode;
        if (!rpcEnabled) {
            self.ctx.$scope.error =
                'Target device is not set!';
        } else {
            if (!isSimulated) {
                if (settings.retrievedMethod == 'rpc') {
                    rpcRetrievedValue(settings
                        .retrievedRPCMethodName,
                        settings.requestTimeout);
                } else if (settings.retrievedMethod ==
                    'attribute') {
                    if (firstRetrieveValue) {
                        subscribeAttributes(
                            "entity", // types.datasourceType.entity, in types.constant.js
                            "DEVICE", //types.entityType.device, in types.constant.js
                            self.ctx.defaultSubscription
                            .targetDeviceId,
                            //settings.retrievedMethod, 
                            settings
                            .retrievedAttributeKey);
                    }
                } else if (settings.retrievedMethod ==
                    'timeseries') {
                    if (firstRetrieveValue) {
                        subscribeTimeseries(
                            "entity", // types.datasourceType.entity, in types.constant.js
                            "DEVICE", //types.entityType.device, in types.constant.js
                            self.ctx.defaultSubscription
                            .targetDeviceId,
                            //settings.retrievedMethod, 
                            settings
                            .retrievedTimeseriesKey);
                    }
                }
            }
        }
        firstRetrieveValue = false;
    }

    retrieveValue();
}

self.onResize = function() {};

self.onDestory = function() {
    if (valueSubscription) {
        self.ctx.subscriptionApi.removeSubscription(
            valueSubscription.id);
    }
};

/*self.onDataUpdated = function() {
    try{
        self.ctx.$scope.convertedValue = self.ctx.data[0].data[0][1];
        self.ctx.$scope.$digest();
    } catch (e) {
        console.log(e);
    }
}*/

/*self.typeParameters = function() {
    return {
        maxDatasources: 1, // Maximum allowed datasources for this widget, -1 - unlimited
        maxDataKeys: 1 //Maximum allowed data keys for this widget, -1 - unlimited
    }
};*/

