{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "title": {
                "title": "Widget title",
                "type": "string",
                "default": ""
            },

            "retrievedMethod": {
                "title": "Retrieve value using method",
                "type": "string",
                "default": "rpc"
            },
            "retrievedAttributeKey": {
                "title": "Retrieved Attribute key",
                "type": "string",
                "default": ""
            },
            "retrievedTimeseriesKey": {
                "title": "Retrieved Timeseries key",
                "type": "string",
                "default": ""
            },
            "retrievedRPCMethodName": {
                "title": "Retrieved value using server-side RPC method name",
                "type": "string",
                "default": ""
            },
            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 5000
            },
            "convertRetrievedValueFunction": {
                "title": "Function body to parse retrieved value, f(retrievedValue)",
                "type": "string",
                "default": "return retrievedValue; //return true;"
            },

            "settingList": {
                "type": "array",
                "title": "Setting items",
                "minItems": 1,
                "maxItems": 30,
                "items": {
                    "type": "object",
                    "properties": {
                        "icon": {
                            "title": "Material icon, e.g., ac_unit, timer, settings and build.",
                            "type": "string",
                            "default": "settings"
                        },
                        "text": {
                            "title": "Text",
                            "type": "string",
                            "default": ""
                        },
                        "isDisabledFunc": {
                            "title": "Method body to detect if it is disabled, f(convertedValue), returns true or false",
                            "type": "string",
                            "default": "return false; //console.log(convertedValue);"
                        },
                        "customActionFunc": {
                            "title": "Method body to action, f(convertedValue, widgetContext, openRightLayout)",
                            "type": "string",
                            "default": "//self.ctx.stateController.openState(state_id, widgetContext, openRightLayout); //self.ctx.stateController.updateState(...);"
                        }
                    },
                    "required": [
                        "text",
                        "isDisabledFunc",
                        "customActionFunc"
                    ]
                }
            }
        },

        "required": [
            "retrievedMethod",
            "convertRetrievedValueFunction",
            "settingList"
        ]
    },
    "form": [
        "title",

        {
            "key": "retrievedMethod",
            "type": "rc-select",
            "multiple": false,
            "items": [{
                    "value": "none",
                    "label": "Don't retrieve"
                },
                {
                    "value": "attribute",
                    "label": "Subscribe for attribute"
                },
                {
                    "value": "timeseries",
                    "label": "Subscribe for timeseries"
                },
                {
                    "value": "rpc",
                    "label": "Call server-dide RPC to get value"
                }
            ]
        },
        {
            "key": "retrievedAttributeKey",
            "condition": "model.retrievedMethod === 'attribute' || model.retrievedMethod === 'SHARED_SCOPE' || model.retrievedMethod === 'SERVER_SCOPE'"
        },
        {
            "key": "retrievedTimeseriesKey",
            "condition": "model.retrievedMethod === 'timeseries'"
        },
        {
            "key": "retrievedRPCMethodName",
            "condition": "model.retrievedMethod === 'rpc'"
        },
        {
            "key": "requestTimeout",
            "condition": "model.retrievedMethod === 'rpc'"
        },
        {
            "key": "convertRetrievedValueFunction",
            "type": "javascript"
        },
        {
            "type": "help",
            "description": "<I><small>Note: The above are the params related to retrieve value from device.</small></I>"
        },

        {
            "key": "settingList",
            "add": "New",
            "style": {
                "add": "btn-success"
            },
            "items": [
                "settingList[].icon",
                {
                    "type": "help",
                    "description": "<I><small>Note: refer to https://mui.com/material-ui/material-icons for more information about material-icons.</small></I>"
                },
                "settingList[].text",
                {
                    "key": "settingList[].isDisabledFunc",
                    "type": "javascript"
                },
                {
                    "type": "help",
                    "description": "<I><small>Note: return true to display this item, return false to not display.</small></I>"
                },
                {
                    "key": "settingList[].customActionFunc",
                    "type": "javascript"
                },
                {
                    "type": "help",
                    "description": "<I><small>Note: Update state, open state, or open new dashboard.</small></I>"
                }
            ]
        }
    ]
}

