descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "title": {
                "title": "Widget title",
                "type": "string",
                "default": ""
            },

            "patternCommonParam": {
                "title": "Pattern common param",
                "type": "string",
                "default": "0"
            },

            "retrieveMethod": {
                "title": "Retrieve string value using method",
                "type": "string",
                "default": "rpc"
            },
            "patternParamOfRetrieveAttributeKey": {
                "title": "Pattern param of retrieved Attribute key",
                "type": "string",
                "default": ""
            },
            "patternParamOfRetrieveTimeseriesKey": {
                "title": "Pattern param of retrieved Timeseries key",
                "type": "string",
                "default": ""
            },
            "patternParamOfRetrieveRPCMethodName": {
                "title": "Pattern param of retrieved value using server-side RPC method name",
                "type": "string",
                "default": ""
            },
            "convertRetrievedAttributeKeyOrMethodNameFunction": {
                "title": "Convert function, f(stateParams, patternParamOfetrievedAttributeKeyOrMethodName, patternCommonParam), returns retireved attribute key, time-series data key or server-side RPC method name",
                "type": "string",
                "default": "return patternParamOfetrievedAttributeKeyOrMethodName + patternCommonParam; /* console.log(value); */"
            },
            "convertRetrievedValueFunction": {
                "title": "Parse value function, f(value), returns string",
                "type": "string",
                "default": "return value;"
            },

            "updateMethod": {
                "title": "Update string value using method",
                "type": "string",
                "default": "rpc"
            },
            "patternParamOfUpdateAttributeKey": {
                "title": "Pattern param of updated Attribute key",
                "type": "string",
                "default": ""
            },
            "patternParamOfUpdateTimeseriesKey": {
                "title": "Pattern param of updated Timeseries key",
                "type": "string",
                "default": ""
            },
            "patternParamOfUpdateRPCMethodName": {
                "title": "Pattern param of updated server-side RPC method name",
                "type": "string",
                "default": ""
            },
            "convertUpdatedAttributeKeyOrMethodNameFunction": {
                "title": "Convert function, f(stateParams, patternParamOfUpdateAttributeKeyOrMethodName, patternCommonParam), returns updated attribute key, time-series data key or server-side RPC method name",
                "type": "string",
                "default": "return patternParamOfUpdateAttributeKeyOrMethodName + patternCommonParam; /* console.log(value); */"
            },
            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 5000
            },
            "convertUpdatedValueFunction": {
                "title": "Convert function, f(value), returns updated value",
                "type": "string",
                "default": "return value;"
            },

            "buttonState": {
                "type": "object",
                "title": "Button state",
                "properties": {
                    "on": {
                        "type": "object",
                        "title": "On state",
                        "properties": {
                            "value": {
                                "title": "On state attribute value",
                                "type": "string",
                                "default": "true",
                                "description": "On state attribute value."
                            },
                            "label": {
                                "title": "On state label",
                                "type": "string",
                                "default": "On state Label"
                            },
                            "isRaised": {
                                "type": "boolean",
                                "title": "Raised",
                                "default": true
                            },
                            "isPrimary": {
                                "type": "boolean",
                                "title": "Primary color",
                                "default": true
                            },
                            "bgColor": {
                                "type": "string",
                                "title": "On state background color",
                                "default": null
                            },
                            "textColor": {
                                "type": "string",
                                "title": "On state text color",
                                "default": null
                            }
                        },
                        "required": [
                            "value",
                            "label"
                        ]
                    },
                    "off": {
                        "type": "object",
                        "title": "Off state",
                        "properties": {
                            "value": {
                                "title": "Off state attribute value",
                                "type": "string",
                                "default": "false",
                                "description": "Off state attribute value."
                            },
                            "label": {
                                "title": "Off state label",
                                "type": "string",
                                "default": "Off state label"
                            },
                            "isRaised": {
                                "type": "boolean",
                                "title": "Raised",
                                "default": true
                            },
                            "isPrimary": {
                                "type": "boolean",
                                "title": "Primary color",
                                "default": false
                            },
                            "bgColor": {
                                "type": "string",
                                "title": "Off state background color",
                                "default": null
                            },
                            "textColor": {
                                "type": "string",
                                "title": "Off state text color",
                                "default": null
                            }
                        },
                        "required": [
                            "value",
                            "label"
                        ]
                    }
                },
                "required": [
                    "on",
                    "off"
                ]
            },
            "showResultMessage": {
                "title": "Show result message",
                "type": "boolean",
                "default": true
            },
            "requiredErrorMessage": {
                "title": "'Required' error message",
                "type": "string",
                "default": ""
            }
        },
        "required": [
            "patternCommonParam",
            "retrieveMethod",
            "convertRetrievedAttributeKeyOrMethodNameFunction",
            "updateMethod",
            "convertUpdatedAttributeKeyOrMethodNameFunction",
            "requestTimeout",
            "buttonState"
        ]
    },
    "form": [
        "title",

        "patternCommonParam",

        {
            "key": "retrieveMethod",
            "type": "rc-select",
            "multiple": false,
            "items": [{
                    "value": "none",
                    "label": "Don't retrieve"
                },
                {
                    "value": "attribute",
                    "label": "Subscribe for attribute"
                },
                {
                    "value": "timeseries",
                    "label": "Subscribe for timeseries"
                },
                {
                    "value": "rpc",
                    "label": "Call server-dide RPC to get value"
                }
            ]
        },
        {
            "key": "patternParamOfRetrieveAttributeKey",
            "condition": "model.retrieveMethod === 'attribute' || model.retrieveMethod === 'SHARED_SCOPE' || model.retrieveMethod === 'SERVER_SCOPE'"
        },
        {
            "key": "patternParamOfRetrieveTimeseriesKey",
            "condition": "model.retrieveMethod === 'timeseries'"
        },
        {
            "key": "patternParamOfRetrieveRPCMethodName",
            "condition": "model.retrieveMethod === 'rpc'"
        },
        {
            "key": "convertRetrievedAttributeKeyOrMethodNameFunction",
            "type": "javascript"
        },
        {
            "key": "convertRetrievedValueFunction",
            "type": "javascript"
        },
        {
            "type": "help",
            "description": "<I><small>Note: Retrieve the above attributes from device.</small></I>"
        },

        {
            "key": "updateMethod",
            "type": "rc-select",
            "multiple": false,
            "items": [{
                    "value": "SHARED_SCOPE",
                    "label": "Update shared attribute"
                },
                {
                    "value": "SERVER_SCOPE",
                    "label": "Update server attribute"
                },
                {
                    "value": "timeseries",
                    "label": "Update timeseries"
                },
                {
                    "value": "rpc",
                    "label": "Call server-side RPC to set value"
                }
            ]
        },
        {
            "key": "patternParamOfUpdateAttributeKey",
            "condition": "model.updateMethod === 'SHARED_SCOPE' || model.updateMethod === 'SERVER_SCOPE'"
        },
        {
            "key": "patternParamOfUpdateTimeseriesKey",
            "condition": "model.updateMethod === 'timeseries'"
        },
        {
            "key": "patternParamOfUpdateRPCMethodName",
            "condition": "model.updateMethod === 'rpc'"
        },
        {
            "key": "convertUpdatedAttributeKeyOrMethodNameFunction",
            "type": "javascript"
        },
        {
            "key": "convertUpdatedValueFunction",
            "type": "javascript"
        },
        {
            "type": "help",
            "description": "<I><small>Note: Call the above method and prameters to update value to device.</small></I>"
        },

        "requestTimeout",

        {
            "key": "buttonState",
            "items": [{
                "key": "buttonState.on",
                "items": [
                    "buttonState.on.value",
                    "buttonState.on.label",
                    "buttonState.on.isRaised",
                    "buttonState.on.isPrimary",
                    {
                        "key": "buttonState.on.bgColor",
                        "type": "color"
                    }, {
                        "key": "buttonState.on.textColor",
                        "type": "color"
                    }
                ]
            }, {
                "key": "buttonState.off",
                "items": [
                    "buttonState.off.value",
                    "buttonState.off.label",
                    "buttonState.off.isRaised",
                    "buttonState.off.isPrimary",
                    {
                        "key": "buttonState.off.bgColor",
                        "type": "color"
                    }, {
                        "key": "buttonState.off.textColor",
                        "type": "color"
                    }
                ]
            }]
        },
        "showResultMessage",
        "requiredErrorMessage"
    ]
}
