.my-tbs-navigation .button-container .card {
   width: 100%;
   height: 100%;
   box-sizing: border-box;
   background-color: #fff;
}

.my-tbs-navigation .button-container .card .content {
   padding: 5px;
   display: flex;
   flex-direction: column;
   align-items: center;
   justify-content: center;
   height: 100%;
   box-sizing: border-box;
   transition: background-color 0.5s;
}

.my-tbs-navigation .button-container .card .content:hover {
    background-color: #ddd;
}

.my-tbs-navigation .button-container .card .description {
    font-size: 0.75em;
    font-weight: normal;
    line-height: 2;
    color: #333333;
    text-transform: uppercase;
}

.my-tbs-navigation .error-container {
    position: absolute;
    top: 2%;
    right: 0;
    left: 0;
    z-index: 4;
    height: 14px;
}

.my-tbs-navigation .error-container .button-error {
    color: #ff3315;
    white-space: nowrap;
}

descriptor.controllerScript:
// v.2.4, 2023-09-25 17:00

self.onInit = function() {
    self.ctx.$scope.onSelectedBtnChange = function(index) {
        //console.log("self.ctx", self.ctx);
        console.log("index=", index);
        
        //console.log("self.ctx.$scope.buttonsSelected=", self.ctx.$scope.buttonsSelected);

        if (self.ctx.$scope.buttonsSelected == index) {
            return;
        } else {
            self.ctx.$scope.buttonsSelected = index;
        }
        
        let entityInfo = self.ctx.actionsApi.getActiveEntityInfo();
        //entityInfo.entityId, entityInfo.entityLabel, entityInfo.entityName
        //console.log(entityInfo);
        
        const id_ = entityInfo ? entityInfo.id : null
            , entityType_ = entityInfo ? entityInfo.entityType : null
            , entityId = {entityType: entityType_, id: id_}
            , entityName = entityInfo ? entityInfo.entityName : null
            , entityLabel = entityInfo ? entityInfo.entityLabel : null;

        if (!self.ctx.isEdit && (index<self.ctx.$scope.buttons.length)) //entityInfo &&
        {
            const descriptor = self.ctx.$scope.buttons[index];
            //console.log("descriptor=", descriptor);
            if (descriptor) {
                //event.stopPropagation();
                // self.ctx.actionsApi.handleWidgetAction($event, descriptor, entityId, entityName);
                self.ctx.actionsApi.handleWidgetAction(null, descriptor,
                    entityId, entityName, null, entityLabel);
            }
        }
    };

    ////self.ctx.ngZone.run(function() {
       init();
    //  self.ctx.detectChanges(true);
    //});
};

function init() {
    let utils       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('utils'));
    //let types       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('types'));
    //let toast       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('toast'));
    //let translate   = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('translate'));
    //let $q          = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('$q'));
    //let http        = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('http'));
    //let attributeService = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('attributeService'));
    //let rpcUpdateStatus = {};
    let settings = utils.deepClone(self.ctx.settings) || {}; 
    
    self.ctx.$scope.toastTargetId = 'input-widget' + utils.guid();
    self.ctx.$scope.showTitle = settings.title && settings.title.length ? true : false; ////utils.defaultValue(settings.title, true);
    self.ctx.$scope.title = settings.title;
    // self.ctx.$scope.position = settings.position;
    // self.ctx.$scope.align = settings.align;
    //self.ctx.$scope.minWidth = settings.minWidth;
    //self.ctx.$scope.isPrimary = settings.isPrimary; //
    self.ctx.$scope.color = settings.color; //
    self.ctx.$scope.bgColor = settings.bgColor;
    self.ctx.$scope.buttons = self.ctx.actionsApi.getActionDescriptors("elementClick");  ////actionDescriptors = ////settings.buttons;
    self.ctx.$scope.buttonsSelected = settings.buttonsSelected;
    if (self.ctx.$scope.buttonsSelected<0) {
        self.ctx.$scope.buttonsSelected = 0;
    } else if (self.ctx.$scope.buttonsSelected>=self.ctx.$scope.buttons.length) {
        self.ctx.$scope.buttonsSelected = self.ctx.$scope.buttons.length-1;
    }
    
    // console.log("actionDescriptors=", self.ctx.$scope.buttons);

    /*self.ctx.$scope.tabGroupStyle = {};
    self.ctx.$scope.customCheckedStyle['min-width'] = '40px';
    self.ctx.$scope.customCheckedStyle['padding']   = '0 2px';
    if (self.ctx.settings.optionStyle.orientation === 'column') {
        self.ctx.$scope.customCheckedStyle['width']     = '100%';
        self.ctx.$scope.customCheckedStyle['margin']    = 0;
    } else {
        self.ctx.$scope.customCheckedStyle['width']     = 100/self.ctx.settings.options.length + '%';
        self.ctx.$scope.customCheckedStyle['margin']    = 0;
    }
    if (self.ctx.settings.isPrimary === false) {
        self.ctx.$scope.tabGroupStyle['background-color']  = self.ctx.$scope.bgColor;
        self.ctx.$scope.tabGroupStyle['color']             = self.ctx.$scope.color;
    }*/

    //console.log("onInit", self.ctx.$scope.align, self.ctx.$scope.position);
}

self.onResize = function() {
    //    min-width: 80%
};

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
};

self.onDestory = function() {
};

