descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "title": {
                "title": "Widget title",
                "type": "string",
                "default": ""
            },
            
            "buttonsSelected": {
                "title": "Buttons selected",
                "type": "integer",
                "default": "0"
            },
            "color": {
                "type": "string",
                "title": "Buttons color",
                "default": ""
            },
            "bgColor": {
                "type": "string",
                "title": "Buttons background color",
                "default": ""
            }
        },
            
        "required": [
            "buttonsSelected"
        ]
    },

    "form": [
        "title",

        "buttonsSelected",
        {
            "key": "bgColor",
            "type": "rc-select",
            "multiple": false,
            "items": [
                {
                    "value": "",
                    "label": "null"
                },
                {
                    "value": "primary",
                    "label": "primary"
                },
                {
                    "value": "accent",
                    "label": "accent"
                },
                {
                    "value": "warn",
                    "label": "warn"
                }
            ]
        },
        {
            "type": "help",
            "description": "Refer to https://material.angular.io/guide/theming#themes for more information about primary, accent and warn."
        },
        {
            "key": "color",
            "type": "rc-select",
            "multiple": false,
            "items": [
                {
                    "value": "",
                    "label": "null"
                },
                {
                    "value": "primary",
                    "label": "primary"
                },
                {
                    "value": "accent",
                    "label": "accent"
                },
                {
                    "value": "warn",
                    "label": "warn"
                }
            ]
        },
        {
            "type": "help",
            "description": "Refer to https://material.angular.io/guide/theming#themes for more information about primary, accent and warn."
        }
    ]
}

